import { SdCardAlert } from "@mui/icons-material";
import { makeStyles } from "@mui/styles";

export default makeStyles((theme) => ({
  movie: {
    padding: "10px",
  },
  links: {
    alignItems: "center",
    fontWeight: "bolder",
    [theme.breakpoints.up("xs")]: {
      display: "flex",
      flexDirection: "column",
    },
    "&hover": {
      cursor: "pointer",
      textDecoration: "none",
    },
  },
  title: {
    color: theme.palette.text.primary,
    textOverflow: "ellipsis",
    width: "230px",
    whiteSpace: "nowrap",
    overflow: "hidden",
    marginTop: "10px",
    marginBottom: "0px",
    textAlign: "center",
  },
  image: {
    borderRadius: "20px",
    height: "300px",
    marginBottom: "10px",
    "&hover": {
      transform: "scale(1.05)",
    },
  },
}));
